import { create } from 'zustand';

interface Files {
    id: number;
    name: string;
}

interface filesStore {
    pictures: Files[];
    fetchfiles: () => Promise<void>;
}

export const usefilesStore = create<filesStore>((set) => ({
    pictures: [],
    fetchfiles: async () => {
        const res = await fetch('/api/files');
        set({ pictures: await res.json() });
    }
}));
